import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

const App = () => {
  const [customers, setCustomers] = useState([]);
  const [listings, setListings] = useState([]);

  useEffect(() => {
    fetch('/api/customers')
      .then(res => res.json())
      .then(data => setCustomers(data.customers || []))
      .catch(() => setCustomers([]));

    fetch('/api/listings')
      .then(res => res.json())
      .then(data => setListings(data.listings || []))
      .catch(() => setListings([]));
  }, []);

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-8">Veritas Customer Showcase</h1>

      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">Invited Customers</h2>
        <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {customers.map(c => (
            <li key={c.id} className="bg-white p-4 shadow rounded">
              <p className="font-bold">{c.name}</p>
              <p className="text-gray-500">{c.email}</p>
            </li>
          ))}
          {customers.length === 0 && <p className="text-gray-400">No invited customers yet.</p>}
        </ul>
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">House Listings</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {listings.map(h => (
            <div key={h.id} className="bg-white p-4 shadow rounded">
              <img src={h.image} alt={h.title} className="w-full h-40 object-cover rounded mb-2"/>
              <h3 className="font-bold text-lg">{h.title}</h3>
              <p className="text-gray-600">{h.price}</p>
              <p className={`mt-1 font-semibold ${h.status === 'Sold' ? 'text-red-500' : 'text-green-500'}`}>
                {h.status}
              </p>
            </div>
          ))}
          {listings.length === 0 && <p className="text-gray-400">No houses listed yet.</p>}
        </div>
      </section>
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
