import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

const speak = (text) => {
  if ('speechSynthesis' in window) {
    const utter = new SpeechSynthesisUtterance(text);
    window.speechSynthesis.speak(utter);
  }
};

const App = () => {
  const [system, setSystem] = useState({users: [], ai: [], subscriptions: [], modules: []});

  useEffect(() => {
    fetch('/api/system')
      .then(res => res.json())
      .then(data => {
        setSystem(data);
        speak("Welcome to Keystone. Let's explore your system.");
      });
  }, []);

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-6">Veritas Guided Demo</h1>
      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Active Modules</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {system.modules.map(m => (
            <div key={m.name} className="bg-white p-4 shadow rounded">
              <p className="font-bold">{m.name}</p>
              <p className="text-gray-500">{m.status}</p>
              <button
                className="mt-2 px-3 py-1 bg-blue-500 text-white rounded"
                onClick={() => speak(`${m.name} module is now active`)}
              >
                Demo
              </button>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">Active Subscribers</h2>
        <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {system.users.map(u => (
            <li key={u.id} className="bg-white p-4 shadow rounded">
              <p className="font-bold">{u.name}</p>
              <p className="text-gray-500">{u.subscription}</p>
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
