/**
 * Expose `State`.
 */

module.exports = State;

/**
 * Initialize a new `State`.
 */

function State() {
  this.fill = 'normal';
  this.stroke = 'normal';
  this.scaleX = 1;
  this.scaleY = 1;
  this.translateX = 0;
  this.translateY = 0;
}